import React from "react";
import "./styles.css";

const ProgressBar = ({ width }) => {
  const isValidWidth = width >= 0 && width <= 100;

  return (
    <div className="container">
      {isValidWidth ? (
        <div className="innerContainer" style={{ width: `${width}%` }}>
          {width}%
        </div>
      ) : (
        <div className="errorMessage">Please enter a value between 0 and 100</div>
      )}
    </div>
  );
};

export default ProgressBar;
